// src/pages/HomePage.js
import React, { useState } from 'react';
import EmotionInput from '../EmotionInput';

const HomePage = () => {
  const [result, setResult] = useState(null);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Duygu Analizi (Metin + YÃ¼z + Ses)</h1>

      {/* EmotionInput artÄ±k onResults propâ€™u alÄ±yor */}
      <EmotionInput onResults={setResult} />

      {result && (
        <div style={{ marginTop: '1.5rem' }}>
          <h3>ðŸ§  DetaylÄ± SonuÃ§lar:</h3>

          {result.channels?.text && (
            <p>
              <strong>Metin:</strong> {result.channels.text.label}{' '}
              (%{Math.round(result.channels.text.score * 100)})
            </p>
          )}
          {result.channels?.face && (
            <p>
              <strong>YÃ¼z:</strong> {result.channels.face.label}{' '}
              (%{Math.round(result.channels.face.score * 100)})
            </p>
          )}
          {result.channels?.audio && (
            <p>
              <strong>Ses:</strong> {result.channels.audio.label}{' '}
              (%{Math.round(result.channels.audio.score * 100)})
            </p>
          )}
          {result.fused_emotion && (
            <p>
              <strong>FÃ¼zyon:</strong> {result.fused_emotion.label}{' '}
              (%{Math.round(result.fused_emotion.score * 100)})
            </p>
          )}

          {/* Ã–nerilen MÃ¼zikler */}
          {result.recommended_music?.length > 0 && (
            <div style={{ marginTop: '1rem' }}>
              <h3>ðŸŽµ Ã–nerilen MÃ¼zikler:</h3>
              <ul>
                {result.recommended_music.map((id) => (
                  <li key={id} style={{ marginBottom: '0.5rem' }}>
                    <button
                      onClick={() =>
                        window.open(
                          `https://www.youtube.com/watch?v=${id}`,
                          '_blank'
                        )
                      }
                    >
                      MÃ¼zik AÃ§
                    </button>
                  </li>
                ))}
              </ul>
            </div>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
