import React, { useState, useRef, useEffect } from 'react';
import '.sty/EmotionInput.css';

export default function EmotionInput({ onResults }) {
  const [text, setText] = useState('');
  const [imageData, setImageData] = useState(null);
  const [useAudio, setUseAudio] = useState(false);
  const [audioChunks, setAudioChunks] = useState([]);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioUrl, setAudioUrl] = useState('');
  const [audioBase64, setAudioBase64] = useState('');
  const [loading, setLoading] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Kamera açma
  const openCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    } catch (err) {
      console.error('Kamera açılamadı:', err);
    }
  };

  // Fotoğraf çekme
  const capturePhoto = () => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
    const data = canvasRef.current.toDataURL('image/png');
    setImageData(data.split(',')[1]);
  };

  // Ses kaydı başlatma
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      recorder.ondataavailable = (e) => setAudioChunks((prev) => [...prev, e.data]);
      recorder.onstop = () => {
        const blob = new Blob(audioChunks, { type: 'audio/wav' });
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);
        const reader = new FileReader();
        reader.readAsDataURL(blob);
        reader.onloadend = () => {
          const base64data = reader.result.split(',')[1];
          setAudioBase64(base64data);
          setUseAudio(true);
        };
      };
      recorder.start();
      setMediaRecorder(recorder);
      setAudioChunks([]);
      setAudioUrl('');
      setAudioBase64('');
      setUseAudio(false);
    } catch (err) {
      console.error('Ses kaydı başlatılamadı:', err);
    }
  };

  // Ses kaydını durdurma
  const stopRecording = () => {
    if (mediaRecorder) mediaRecorder.stop();
  };

  // Analiz et
  const analyze = async () => {
    if (useAudio && !audioBase64) {
      alert('Önce ses kaydını durdurun ve yükleyin.');
      return;
    }
    setLoading(true);
    try {
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          text,
          face: imageData,
          use_audio: useAudio,
          audio: audioBase64,
        }),
      });
      const result = await response.json();
      onResults(result);
    } catch (err) {
      console.error('Analiz hatası:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="emotion-input">
      <textarea
        placeholder="Metni buraya gir..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />
      <div className="camera-controls">
        <button onClick={openCamera}>Kamerayı Aç</button>
        <button onClick={capturePhoto}>Fotoğrafı Çek</button>
        <video ref={videoRef} width="320" height="240" />
        <canvas ref={canvasRef} width="320" height="240" style={{ display: 'none' }} />
      </div>
      <div className="audio-controls">
        <button onClick={startRecording}>Ses Kaydı Başlat</button>
        <button onClick={stopRecording} disabled={!mediaRecorder}>Ses Kaydını Durdur</button>
        {audioUrl && (
          <audio controls src={audioUrl} />
        )}
      </div>
      <button onClick={analyze} disabled={loading}>
        {loading ? 'Analiz Ediliyor...' : 'Analiz Et'}
      </button>
    </div>
  );
}
