import React, { useState } from 'react';
import EmotionInput from '../components/EmotionInput';
import '../styles/HomePage.css';

export default function HomePage() {
  const [result, setResult] = useState(null);

  // Baloncukları oluşturacak helper
  const bubbles = Array.from({ length: 30 }).map((_, i) => ({
    id: i,
    left: Math.random() * 100,                // yatay pozisyon %
    size: 10 + Math.random() * 30,             // px
    duration: 10 + Math.random() * 10,         // sn
  }));

  // Notaları parametreleriyle oluştur
  const notes = Array.from({ length: 20 }).map((_, i) => ({
    id: i,
    left: Math.random() * 100,
    fallDur: 8 + Math.random() * 6,
    swayDur: 3 + Math.random() * 3,
    size: 16 + Math.random() * 24,
  }));

  return (
    <div className="homepage">
      {/* 1) Baloncuk Partikülleri */}
      <div className="bubbles-container">
        {bubbles.map(b => (
          <span
            key={b.id}
            className="bubble"
            style={{
              left: `${b.left}%`,
              width: `${b.size}px`,
              height: `${b.size}px`,
              '--dur': `${b.duration}s`
            }}
          />
        ))}
      </div>

      {/* 2) Müzik Notaları */}
      <div className="notes-container">
        {notes.map(n => (
          <span
            key={n.id}
            className="note"
            style={{
              left: `${n.left}%`,
              '--fall-dur': `${n.fallDur}s`,
              '--sway-dur': `${n.swayDur}s`,
              '--note-size': `${n.size}px`
            }}
          >
            ♪
          </span>
        ))}
      </div>

      {/* İçerik */}
      <h1>Duygu Analizi + Müzik Öneri</h1>
      <div className="emotion-section">
        <EmotionInput onResults={setResult} />
      </div>
      {result && (
        <div className="results">
          {/* Sonuç Detayları */}
          <div className="details">
            {/* ... */}
          </div>
          {/* Müzik Kartları */}
          <div className="music-list">
            {/* ... */}
          </div>
        </div>
      )}
    </div>
  );
}
