import React, { useRef, useState } from 'react';
import { BACKEND_URL } from './config';
import './styles/EmotionInput.css';

const EmotionInput = ({ text, result, setResult }) => {
  const videoRef = useRef(null);
  const [loading, setLoading] = useState(false);

  const captureAndAnalyze = async (withAudio = false) => {
    setLoading(true);
    try {
      let base64Image = null;
      if (videoRef.current && videoRef.current.srcObject) {
        const video = videoRef.current;
        const canvas = document.createElement('canvas');
        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;
        canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
        base64Image = canvas.toDataURL('image/jpeg').split(',')[1];
      }

      const response = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          text: text || null,
          face: base64Image,
          use_audio: withAudio
        })
      });
      const data = await response.json();
      console.log('Gelen veri:', data);
      setResult(data);
    } catch (error) {
      console.error('Analiz hatası:', error);
      alert('Sunucuya bağlanılamadı.');
    }
    setLoading(false);
  };

  const startCamera = () => {
    navigator.mediaDevices.getUserMedia({ video: true })
      .then(stream => { videoRef.current.srcObject = stream; })
      .catch(err => {
        console.error('Kamera açılamadı:', err);
        alert('Kameraya erişilemedi.');
      });
  };

  return (
    <div className="emotion-input-container">
      <button onClick={startCamera}>Kamerayı Aç</button>
      <video ref={videoRef} autoPlay />

      <div>
        <button
          onClick={() => captureAndAnalyze(false)}
          disabled={loading}
        >
          {loading ? 'Analiz Ediliyor...' : 'Kareyi Yakala ve Analiz Et'}
        </button>

        <button
          onClick={() => captureAndAnalyze(true)}
          disabled={loading}
        >
          {loading ? 'Ses Analizi Yapılıyor...' : 'Mikrofonla Ses Analizi Yap'}
        </button>
      </div>

      {/* Eğer API’den gelen öneri listesi varsa, videoları göster */}
      {result?.recommended_music?.length > 0 && (
        <div className="video-grid">
          {result.recommended_music.map(videoId => (
            <iframe
              key={videoId}
              src={`https://www.youtube.com/embed/${videoId}?autoplay=1`}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              title={videoId}
            />
          ))}
        </div>
      )}
    </div>
  );
};

export default EmotionInput;
