import React, { useState } from 'react';
import EmotionInput from '../components/EmotionInput';
import '../styles/HomePage.css';

export default function HomePage() {
  const [result, setResult] = useState(null);

  // üéº 20 nota i√ßin rastgele parametreler olu≈ütur
  const notes = Array.from({ length: 20 }).map((_, i) => ({
    id: i,
    left: Math.random() * 100,              // yatay y√ºzde konum
    fallDur: 8 + Math.random() * 6,         // 8‚Äì14 saniye arasƒ± d√º≈üme s√ºresi
    swayDur: 3 + Math.random() * 3,         // 3‚Äì6 saniye arasƒ± yalpalanma s√ºresi
    size: 16 + Math.random() * 24,          // 16‚Äì40px arasƒ± ikon boyutu
    color: `hsl(${Math.random() * 360}, 80%, 75%)` // rastgele pastel renk
  }));

  return (
    <div className="homepage">
      {/* üéº Arka planda akan notalar */}
      <div className="notes-container">
        {notes.map(n => (
          <span
            key={n.id}
            className="note"
            style={{
              left: `${n.left}%`,           // CSS deƒüi≈ükenlerini inline set ediyoruz
              '--fall-dur': `${n.fallDur}s`,
              '--sway-dur': `${n.swayDur}s`,
              '--note-size': `${n.size}px`,
              '--note-color': n.color
            }}
          >
            ‚ô™
          </span>
        ))}
      </div>

      {/* Ba≈ülƒ±k */}
      <h1>Duygu Analizi + M√ºzik √ñneri</h1>

      {/* üìù Duygu Giri≈ü B√∂l√ºm√º */}
      <div className="emotion-section">
        <EmotionInput onResults={setResult} />
      </div>

      {/* üìä Analiz Sonu√ßlarƒ± ve M√ºzik Kartlarƒ± */}
      {result && (
        <div className="results">
          <div className="details">
            {result.channels?.text && (
              <p>
                üìÑ Metin: {result.channels.text.label} (
                %{Math.round(result.channels.text.score * 100)})
              </p>
            )}
            {result.channels?.face && (
              <p>
                üì∑ Y√ºz: {result.channels.face.label} (
                %{Math.round(result.channels.face.score * 100)})
              </p>
            )}
            {result.fused_emotion && (
              <p>
                üß† F√ºzyon: {result.fused_emotion.label} (
                %{Math.round(result.fused_emotion.score * 100)})
              </p>
              
            )}
          </div>

          <div className="music-list">
            {result.recommended_music.map((uri) => {
              const trackId = uri.split(':').pop();
              return (
                <div key={trackId} className="music-card">
                  <iframe
                    title={trackId}
                    src={`https://open.spotify.com/embed/track/${trackId}`}
                    allow="autoplay; encrypted-media"
                  />
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
