import React, { useState } from 'react';
import EmotionInput from '../components/EmotionInput';
import '../styles/HomePage.css';
// Recharts imports
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';

export default function HomePage() {
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [feedbacks, setFeedbacks] = useState({});

  // üéº 20 nota i√ßin rastgele parametreler olu≈ütur
  const notes = Array.from({ length: 20 }).map((_, i) => ({
    id: i,
    left: Math.random() * 100,
    fallDur: 8 + Math.random() * 6,
    swayDur: 3 + Math.random() * 3,
    size: 16 + Math.random() * 24,
    color: `hsl(${Math.random() * 360}, 80%, 75%)`
  }));

  // API'den √ßaƒürƒ± yapan fonksiyon
  const handleAnalyze = async (payload) => {
    setLoading(true);
    setError(null);
    try {
      const res = await fetch('/api/analyze', {
        method: 'POST',
        body: payload,
      });
      if (!res.ok) throw new Error('Sunucu hatasƒ±: ' + res.status);
      const json = await res.json();
      setResult(json);
    } catch (e) {
      console.error(e);
      setError('Analiz sƒ±rasƒ±nda bir hata olu≈ütu. L√ºtfen tekrar deneyin.');
    } finally {
      setLoading(false);
    }
  };

  // Grafiƒüe veri
  const chartData = result
    ? [
        result.channels.text && { name: 'Metin', value: Math.round(result.channels.text.score * 100) },
        result.channels.audio && { name: 'Ses', value: Math.round(result.channels.audio.score * 100) },
        result.channels.face && { name: 'Y√ºz', value: Math.round(result.channels.face.score * 100) }
      ].filter(Boolean)
    : [];

  const handleFeedback = async (trackId, liked) => {
    if (feedbacks[trackId] !== undefined) return;
    try {
      await fetch('/api/feedback', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ track_id: trackId, emotion: result.fused_emotion.label, liked }),
      });
      setFeedbacks(prev => ({ ...prev, [trackId]: liked }));
    } catch {
      // hatayƒ± gizle
    }
  };

  return (
    <div className="homepage">
      {/* üéº Arka planda akan notalar */}
      <div className="notes-container">
        {notes.map(n => (
          <span
            key={n.id}
            className="note"
            style={{
              left: `${n.left}%`,
              '--fall-dur': `${n.fallDur}s`,
              '--sway-dur': `${n.swayDur}s`,
              '--note-size': `${n.size}px`,
              '--note-color': n.color,
            }}
          >
            ‚ô™
          </span>
        ))}
      </div>

      {/* Ba≈ülƒ±k */}
      <h1>Duygu Analizi + M√ºzik √ñneri</h1>

      {/* üìù Duygu Giri≈ü B√∂l√ºm√º */}
      <div className="emotion-section">
        {/* EmotionInput, onAnalyze ve onError props‚Äôu ile */}
        <EmotionInput onAnalyze={handleAnalyze} onError={setError} />
      </div>

      {/* Y√ºkleniyor Spinner */}
      {loading && <div className="spinner">Y√ºkleniyor...</div>}

      {/* Hata Mesajƒ± */}
      {error && <div className="error-message">{error}</div>}

      {/* üìä Analiz Sonu√ßlarƒ±, M√ºzik Kartlarƒ± ve Geri Bildirim */}
      {result && !loading && (
        <div className="results">
          <div className="details">
            {result.channels?.text && (
              <p>üìÑ Metin: {result.channels.text.label} (%{Math.round(result.channels.text.score * 100)})</p>
            )}
            {result.channels?.face && (
              <p>üì∑ Y√ºz: {result.channels.face.label} (%{Math.round(result.channels.face.score * 100)})</p>
            )}
            {result.fused_emotion && (
              <p>üß† F√ºzyon: {result.fused_emotion.label} (%{Math.round(result.fused_emotion.score * 100)})</p>
            )}
          </div>

          <div className="results-content">
            <div className="music-list">
              {result.recommended_music.map(uri => {
                const trackId = uri.split(':').pop();
                return (
                  <div key={trackId} className="music-card feedback-card">
                    <iframe
                      title={trackId}
                      src={`https://open.spotify.com/embed/track/${trackId}`}
                      allow="autoplay; encrypted-media"
                    />
                    <div className="feedback-buttons">
                      <button onClick={() => handleFeedback(trackId, true)} disabled={feedbacks[trackId] !== undefined} className={feedbacks[trackId] === true ? 'liked' : ''}>üëç</button>
                      <button onClick={() => handleFeedback(trackId, false)} disabled={feedbacks[trackId] !== undefined} className={feedbacks[trackId] === false ? 'disliked' : ''}>üëé</button>
                    </div>
                  </div>
                );
              })}
            </div>

            <div className="chart-container">
              <h4 style={{ textAlign: 'center', marginBottom: '8px' }}>
                F√ºzyon Duygu: {result.fused_emotion.label.toUpperCase()}
              </h4>
              <ResponsiveContainer width="100%" height={200}>
                <BarChart data={chartData} margin={{ top: 20, right: 30, left: 0, bottom: 0 }}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis domain={[0, 100]} tickFormatter={v => `${v}%`} />
                  <Tooltip formatter={value => `${value}%`} />
                  <Bar dataKey="value" fill="#8884d8" />
                </BarChart>
              </ResponsiveContainer>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
