// src/EmotionInput.js

import React, { useRef, useState, useEffect } from 'react';
import { BACKEND_URL } from './config';
import './styles/EmotionInput.css';

export default function EmotionInput({ result, setResult }) {
  const videoRef = useRef(null);

  // Text
  const [text, setText] = useState("");

  // Face
  const [stream, setStream] = useState(null);
  const [faceImage, setFaceImage] = useState(null);

  // Audio
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioBase64, setAudioBase64] = useState(null);
  const [isRecordingAudio, setIsRecordingAudio] = useState(false);

  // Loading tüm analizler için
  const [loading, setLoading] = useState(false);

  // Video <-> stream bind
  useEffect(() => {
    if (stream && videoRef.current) {
      videoRef.current.srcObject = stream;
    }
  }, [stream]);

  // Component unmount -> stream / audio stream kapat
  useEffect(() => {
    return () => {
      if (stream) stream.getTracks().forEach(t => t.stop());
      if (mediaRecorder && mediaRecorder.stream) {
        mediaRecorder.stream.getTracks().forEach(t => t.stop());
      }
    };
  }, [stream, mediaRecorder]);

  // — Kamera kontrollleri —

  const startCamera = async () => {
    if (stream) stream.getTracks().forEach(t => t.stop());
    setFaceImage(null);
    try {
      const s = await navigator.mediaDevices.getUserMedia({ video: true });
      setStream(s);
    } catch (err) {
      console.error("Kamera açma hatası", err);
      alert("Kameraya erişilemedi: " + err.message);
    }
  };

  const captureFace = () => {
    if (!stream || !videoRef.current) return;
    const video = videoRef.current;
    const canvas = document.createElement("canvas");
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext("2d").drawImage(video, 0, 0, canvas.width, canvas.height);
    const b64 = canvas.toDataURL("image/jpeg").split(",")[1];
    setFaceImage(b64);
    // kamerayı kapat
    stream.getTracks().forEach(t => t.stop());
    setStream(null);
  };

  // — Ses kaydı kontrollleri —

  const startAudioRecording = async () => {
    if (isRecordingAudio) return;
    try {
      const audioStream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(audioStream);
      const chunks = [];
      recorder.ondataavailable = e => chunks.push(e.data);
      recorder.onstop = () => {
        const blob = new Blob(chunks, { type: "audio/webm" });
        const reader = new FileReader();
        reader.onloadend = () => {
          const dataUrl = reader.result; // data:audio/webm;base64,...
          setAudioBase64(dataUrl.split(",")[1]);
        };
        reader.readAsDataURL(blob);
        // artık mikrofonu kapat
        audioStream.getTracks().forEach(t => t.stop());
      };
      recorder.start();
      setMediaRecorder(recorder);
      setIsRecordingAudio(true);

      // Otomatik durdurma (örneğin 10s sonra)
      setTimeout(() => {
        recorder.stop();
        setIsRecordingAudio(false);
      }, 10000);
    } catch (err) {
      console.error("Ses kaydı hatası", err);
      alert("Mikrofona erişilemedi: " + err.message);
    }
  };

  // — Analiz gönderimi —

  const analyzeAll = async () => {
    setLoading(true);
    try {
      const payload = {
        text: text.trim() || null,
        face: faceImage,
        audio: audioBase64,
        use_audio: !!audioBase64
      };
      console.log("Gönderilen payload:", payload);

      const res = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      console.log("API cevabı:", data);
      setResult(data);
    } catch (err) {
      console.error("Analiz hatası:", err);
      alert("Sunucuya bağlanılamadı.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="emotion-input-container">
      {/* Metin */}
      <textarea
        placeholder="Metni buraya gir..."
        value={text}
        onChange={e => setText(e.target.value)}
        style={{ width: "100%", maxWidth: 400, marginBottom: "1rem" }}
      />

      {/* Yüz (Kamera) */}
      {!stream && (
        <button onClick={startCamera}>Kamerayı Aç</button>
      )}
      {stream && (
        <div style={{ margin: "1rem 0" }}>
          <video ref={videoRef} autoPlay style={{ width: 300 }} />
          <button onClick={captureFace} style={{ display: "block", marginTop: 8 }}>
            Kareyi Yakala
          </button>
        </div>
      )}
      {faceImage && (
        <div style={{ margin: "1rem 0" }}>
          <img
            src={`data:image/jpeg;base64,${faceImage}`}
            alt="Yüz önizleme"
            style={{ width: 300, border: "2px solid #444" }}
          />
        </div>
      )}

      {/* Ses */}
      <button
        onClick={startAudioRecording}
        disabled={isRecordingAudio}
        style={{ display: "block", margin: "1rem 0" }}
      >
        {isRecordingAudio ? "Kaydediliyor…" : "Ses Kaydı Başlat"}
      </button>
      {audioBase64 && <small>🗲 Ses kaydı hazır ({Math.round((audioBase64.length*3)/4/1024)} KB)</small>}

      {/* Analiz Et */}
      <button
        onClick={analyzeAll}
        disabled={loading}
        style={{ display: "block", margin: "1rem auto" }}
      >
        {loading ? "Analiz Ediliyor…" : "Analiz Et"}
      </button>

      {/* YouTube Önerileri */}
      {result?.recommended_music?.length > 0 && (
        <div className="video-grid" style={{ marginTop: "1.5rem" }}>
          {result.recommended_music.map(id => (
            <iframe
              key={id}
              src={`https://www.youtube.com/embed/${id}?autoplay=1`}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              title={id}
            />
          ))}
        </div>
      )}
    </div>
  );
}
