import React, { useState, useRef } from 'react';
import './styles/EmotionInput.css';

export default function EmotionInput({ onResults }) {
  const [text, setText] = useState('');
  const [imageData, setImageData] = useState(null);
  const [audioChunks, setAudioChunks] = useState([]);
  const [mediaRecorder, setMediaRecorder] = useState(null);
  const [audioUrl, setAudioUrl] = useState('');
  const [audioBase64, setAudioBase64] = useState('');
  const [loading, setLoading] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);

  // Kamera açma
  const openCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      videoRef.current.play();
    } catch (err) {
      console.error('Kamera açılamadı:', err);
    }
  };

  // Fotoğraf çekme
  const capturePhoto = () => {
    const ctx = canvasRef.current.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0, canvasRef.current.width, canvasRef.current.height);
    const data = canvasRef.current.toDataURL('image/png');
    setImageData(data.split(',')[1]);
  };

  // Ses kaydı başlatma
  const startRecording = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
      const recorder = new MediaRecorder(stream);
      setAudioChunks([]);
      recorder.ondataavailable = (e) => {
        if (e.data && e.data.size > 0) {
          setAudioChunks((prev) => [...prev, e.data]);
        }
      };
      recorder.start();
      setMediaRecorder(recorder);
      setAudioUrl('');
      setAudioBase64('');
    } catch (err) {
      console.error('Ses kaydı başlatılamadı:', err);
    }
  };

  // Ses kaydını durdurma ve işleme
  const stopRecording = () => {
    if (mediaRecorder) {
      mediaRecorder.onstop = () => {
        const blob = new Blob(audioChunks, { type: audioChunks[0]?.type || 'audio/webm' });
        const url = URL.createObjectURL(blob);
        setAudioUrl(url);
        const reader = new FileReader();
        reader.onloadend = () => {
          const b64 = reader.result.split(',')[1];
          setAudioBase64(b64);
        };
        reader.readAsDataURL(blob);
      };
      mediaRecorder.stop();
      setMediaRecorder(null);
    }
  };

  // Analiz et
  const analyze = async () => {
    if (audioChunks.length > 0 && !audioBase64) {
      alert('Ses kaydı işleniyor, lütfen birkaç saniye bekleyip tekrar durdurun.');
      return;
    }
    setLoading(true);
    try {
      const response = await fetch('/api/analyze', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          text,
          face: imageData,
          use_audio: Boolean(audioBase64),
          audio: audioBase64,
        }),
      });
      const result = await response.json();
      onResults(result);
    } catch (err) {
      console.error('Analiz hatası:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="emotion-input">
      <textarea
        placeholder="Metni buraya gir..."
        value={text}
        onChange={(e) => setText(e.target.value)}
      />

      <div className="camera-controls">
        <button onClick={openCamera}>Kamerayı Aç</button>
        <button onClick={capturePhoto}>Fotoğrafı Çek</button>
        <video ref={videoRef} width="320" height="240" />
        <canvas
          ref={canvasRef}
          width="320"
          height="240"
          style={{ display: 'none' }}
        />
      </div>

      <div className="audio-controls">
        <button onClick={startRecording} disabled={mediaRecorder !== null}>
          Ses Kaydı Başlat
        </button>
        <button onClick={stopRecording} disabled={mediaRecorder === null}>
          Ses Kaydını Durdur
        </button>
        {audioUrl && <audio controls src={audioUrl} />}
      </div>

      <button
        onClick={analyze}
        disabled={
          loading || (audioChunks.length > 0 && !audioBase64)
        }
      >
        {loading ? 'Analiz Ediliyor...' : 'Analiz Et'}
      </button>
    </div>
  );
}
