// src/EmotionInput.js

import React, { useRef, useState } from 'react';
import { BACKEND_URL } from './config';
import './styles/EmotionInput.css';

export default function EmotionInput({ result, setResult }) {
  const videoRef = useRef(null);
  const [stream, setStream] = useState(null);
  const [faceImage, setFaceImage] = useState(null);      // Yakalanan kare
  const [recording, setRecording] = useState(false);
  const [text, setText] = useState('');
  const [loading, setLoading] = useState(false);

  // Kamera başlatır ve stream state’ine ayarlar
  const startCamera = async () => {
    try {
      const s = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = s;
      setStream(s);
      setFaceImage(null);  // Önceki varsa temizle
    } catch (err) {
      console.error('Kamera açılamadı:', err);
      alert('Kameraya erişilemedi.');
    }
  };

  // Kareyi yakalar, base64’e çevirir, kamerayı kapatır
  const captureFace = () => {
    if (!stream) return;
    const video = videoRef.current;
    const canvas = document.createElement('canvas');
    canvas.width = video.videoWidth;
    canvas.height = video.videoHeight;
    canvas.getContext('2d').drawImage(video, 0, 0, canvas.width, canvas.height);
    const dataUrl = canvas.toDataURL('image/jpeg');
    setFaceImage(dataUrl.split(',')[1]);  // sadece base64 kısmı

    // Kamerayı kapat
    stream.getTracks().forEach((track) => track.stop());
    setStream(null);
  };

  // 5s ses kaydını başlatıp state’i günceller
  const startRecording = () => {
    setRecording(true);
    setTimeout(() => setRecording(false), 5000);
  };

  // Tüm kanalları backend’e gönderip analiz al
  const analyzeAll = async () => {
    setLoading(true);
    try {
      const payload = {
        text: text || null,
        face: faceImage,
        use_audio: recording
      };

      const res = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(payload)
      });
      const data = await res.json();
      setResult(data);
    } catch (err) {
      console.error('Analiz hatası:', err);
      alert('Sunucuya bağlanılamadı.');
    }
    setLoading(false);
  };

  return (
    <div className="emotion-input-container">
      {/* Metin */}
      <textarea
        placeholder="Metni buraya gir..."
        value={text}
        onChange={(e) => setText(e.target.value)}
        style={{ width: '100%', maxWidth: '300px', marginBottom: '1rem' }}
      />

      {/* Yüz */}
      <div style={{ marginBottom: '1rem' }}>
        {!stream && !faceImage && (
          <button onClick={startCamera}>Kamerayı Aç</button>
        )}
        {stream && (
          <>
            <video ref={videoRef} autoPlay style={{ width: 300, marginTop: '1rem' }} />
            <div>
              <button onClick={captureFace} style={{ marginTop: '0.5rem' }}>
                Fotoğraf Çek
              </button>
            </div>
          </>
        )}
        {faceImage && (
          <img
            src={`data:image/jpeg;base64,${faceImage}`}
            alt="Yakalanan kare"
            style={{ width: 300, marginTop: '1rem', border: '2px solid #444' }}
          />
        )}
      </div>

      {/* Ses */}
      <div style={{ marginBottom: '1rem' }}>
        <button onClick={startRecording} disabled={recording}>
          {recording ? 'Ses Kaydediliyor…' : 'Ses Kaydı (5s)'}
        </button>
      </div>

      {/* Analiz Et */}
      <button
        className="analyze-button"
        onClick={analyzeAll}
        disabled={loading}
      >
        {loading ? 'Analiz Ediliyor…' : 'Analiz Et'}
      </button>

      {/* Sonuçlar ve video grid */}
      {result?.recommended_music?.length > 0 && (
        <div className="video-grid" style={{ marginTop: '1.5rem' }}>
          {result.recommended_music.map((id) => (
            <iframe
              key={id}
              title={id}
              src={`https://www.youtube.com/embed/${id}?autoplay=1`}
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
            />
          ))}
        </div>
      )}
    </div>
  );
}
