import React, { useState } from 'react';
import EmotionInput from '../EmotionInput';

const HomePage = () => {
  const [text, setText] = useState('');
  const [result, setResult] = useState(null);

  return (
    <div style={{ padding: '2rem' }}>
      <h1>Duygu Analizi (Metin + YÃ¼z + Ses)</h1>

      <textarea
        rows={4}
        cols={50}
        value={text}
        onChange={(e) => setText(e.target.value)}
        placeholder="Metni buraya giriniz..."
        style={{ display: 'block', marginBottom: '1rem' }}
      />

      {/* ArtÄ±k result da prop olarak geÃ§iyoruz */}
      <EmotionInput text={text} result={result} setResult={setResult} />

      {result && (
        <div style={{ marginTop: '2rem' }}>
          <h3>ðŸ§  Tahmin Edilen Duygular:</h3>
          {result.emotions?.text && (
            <p>
              <strong>Metin:</strong> {result.emotions.text.label} (
              %{Math.round(result.emotions.text.score * 100)})
            </p>
          )}
          {result.emotions?.face && (
            <p>
              <strong>YÃ¼z:</strong> {result.emotions.face.label} (
              %{Math.round(result.emotions.face.score * 100)})
            </p>
          )}
          {result.emotions?.audio && (
            <p>
              <strong>Ses:</strong> {result.emotions.audio.label} (
              %{Math.round(result.emotions.audio.score * 100)})
            </p>
          )}
          {result.fused_emotion && (
            <p>
              <strong>BirleÅŸik Duygu (Fused):</strong>{' '}
              {result.fused_emotion.label} (
              %{Math.round(result.fused_emotion.score * 100)})
            </p>
          )}
        </div>
      )}
    </div>
  );
};

export default HomePage;
