import React, { useState } from 'react';
import EmotionInput from '../components/EmotionInput';
import '../styles/HomePage.css';

export default function HomePage() {
  const [result, setResult] = useState(null);
  const [playing, setPlaying] = useState(null);

  return (
    <div className="homepage">
      {/* M√ºzik notalarƒ± animasyonu */}
      <div className="notes-container">
        {Array.from({ length: 20 }).map((_, i) => (
          <span key={i} className="note">‚ô™</span>
        ))}
      </div>

      <h1>Duygu Analizi + M√ºzik √ñneri</h1>
      <EmotionInput onResults={setResult} />

      {result && (
        <div className="results">
          <div className="details">
            {result.channels?.text && (
              <p>üìÑ Metin: {result.channels.text.label} (%{Math.round(result.channels.text.score * 100)})</p>
            )}
            {result.channels?.face && (
              <p>üì∑ Y√ºz: {result.channels.face.label} (%{Math.round(result.channels.face.score * 100)})</p>
            )}
            {result.fused_emotion && (
              <p>üß† F√ºzyon: {result.fused_emotion.label} (%{Math.round(result.fused_emotion.score * 100)})</p>
            )}
          </div>

          <div className="music-list">
            {result.recommended_music.map((uri) => {
              const trackId = uri.split(':').pop();
              const isPlaying = playing === trackId;

              return (
                <div key={trackId} className="music-card">
                  {/* Play/Pause d√ºƒümesi */}
                  <button
                    className="play-btn"
                    onClick={() => setPlaying(isPlaying ? null : trackId)}
                  >
                    {isPlaying ? '‚è∏' : '‚ñ∂Ô∏è'}
                  </button>

                  {/* G√∂m√ºl√º Spotify oynatƒ±cƒ± */}
                  {isPlaying && (
                    <iframe
                      className="spotify-embed"
                      title={trackId}
                      src={`https://open.spotify.com/embed/track/${trackId}?autoplay=1`}
                      width="100%"
                      height="80"
                      frameBorder="0"
                      allow="autoplay; encrypted-media"
                    />
                  )}
                </div>
              );
            })}
          </div>
        </div>
      )}
    </div>
  );
}
