import React from 'react';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Title,
  Legend
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, BarElement, Title, Tooltip, Legend);

export default function EmotionChart({ channels }) {
  const labels = Object.keys(channels);
  const data = {
    labels,
    datasets: [
      {
        label: 'Duygu Skoru',
        data: labels.map(l => Math.round(channels[l].score * 100)),
        backgroundColor: labels.map(l => {
          switch (l) {
            case 'text': return 'rgba(75, 192, 192, 0.6)';
            case 'face': return 'rgba(255, 205, 86, 0.6)';
            case 'audio': return 'rgba(54, 162, 235, 0.6)';
            default: return 'rgba(201, 203, 207, 0.6)';
          }
        })
      }
    ]
  };

  const options = {
    indexAxis: 'y',
    responsive: true,
    plugins: {
      legend: { display: false },
      title: { display: true, text: 'Kanal Bazlı Duygu Skorları' }
    },
    scales: {
      x: { beginAtZero: true, max: 100 }
    }
  };

  return <Bar data={data} options={options} />;
}
