import React, { useState } from 'react';
import EmotionInput from '../components/EmotionInput';
import '../styles/HomePage.css';
import { BACKEND_URL } from '../config';

export default function HomePage() {
  const [result, setResult] = useState(null);
  const [feedbackText, setFeedbackText] = useState('');
  const [feedbackSent, setFeedbackSent] = useState(false);

  // üéº 20 nota i√ßin rastgele parametreler olu≈ütur
  const notes = Array.from({ length: 20 }).map((_, i) => ({
    id: i,
    left: Math.random() * 100,
    fallDur: 8 + Math.random() * 6,
    swayDur: 3 + Math.random() * 3,
    size: 16 + Math.random() * 24,
    color: `hsl(${Math.random() * 360}, 80%, 75%)`
  }));

  // Genel geri bildirim g√∂nder
  const sendGeneralFeedback = async () => {
    if (!feedbackText.trim()) return;
    try {
      await fetch(`${BACKEND_URL}/api/feedback`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ feedback: feedbackText })
      });
      setFeedbackSent(true);
    } catch (err) {
      console.error('Feedback error', err);
    }
  };

  return (
    <div className="homepage">
      {/* üéº Arka planda akan notalar */}
      <div className="notes-container">
        {notes.map(n => (
          <span
            key={n.id}
            className="note"
            style={{
              left: `${n.left}%`,           
              '--fall-dur': `${n.fallDur}s`,
              '--sway-dur': `${n.swayDur}s`,
              '--note-size': `${n.size}px`,
              '--note-color': n.color
            }}
          >
            ‚ô™
          </span>
        ))}
      </div>

      {/* Ba≈ülƒ±k */}
      <h1>Duygu Analizi + M√ºzik √ñneri</h1>

      {/* üìù Duygu Giri≈ü B√∂l√ºm√º */}
      <div className="emotion-section">
        <EmotionInput onResults={setResult} />
      </div>

      {/* üìä Analiz Sonu√ßlarƒ± ve M√ºzik Kartlarƒ± */}
      {result && (
        <>
          <div className="results">
            <div className="details">
              {result.channels?.text && (
                <p>üìÑ Metin: {result.channels.text.label} (%{Math.round(result.channels.text.score * 100)})</p>
              )}
              {result.channels?.face && (
                <p>üì∑ Y√ºz: {result.channels.face.label} (%{Math.round(result.channels.face.score * 100)})</p>
              )}
              {result.fused_emotion && (
                <p>üß† F√ºzyon: {result.fused_emotion.label} (%{Math.round(result.fused_emotion.score * 100)})</p>
              )}
            </div>

            <div className="music-list">
              {result.recommended_music.map((uri) => {
                const trackId = uri.split(':').pop();
                return (
                  <div key={trackId} className="music-card">
                    <iframe
                      title={trackId}
                      src={`https://open.spotify.com/embed/track/${trackId}`}
                      allow="autoplay; encrypted-media"
                    />
                  </div>
                );
              })}
            </div>
          </div>

          {/* Genel Geri Bildirim Alanƒ± */}
          <div className="general-feedback">
            <h3>D√º≈ü√ºncelerinizi merak ediyoruz</h3>
            <textarea
              className="feedback-textarea"
              placeholder="D√º≈ü√ºncelerinizi bizimle payla≈üƒ±n..."
              value={feedbackText}
              onChange={e => setFeedbackText(e.target.value)}
              disabled={feedbackSent}
            />
            <button
              className="feedback-button"
              onClick={sendGeneralFeedback}
              disabled={feedbackSent}
            >
              {feedbackSent ? 'Te≈üekk√ºrler!' : 'G√∂nder'}
            </button>
          </div>
        </>
      )}
    </div>
  );
}
