import React, { useState, useRef, useEffect } from 'react';
import { BACKEND_URL } from '../config';
import './styles/EmotionInput.css';

export default function EmotionInput({ onResults }) {
  const [text, setText] = useState('');
  const [imageData, setImageData] = useState(null);
  const [recognizing, setRecognizing] = useState(false);
  const [loading, setLoading] = useState(false);

  const videoRef = useRef(null);
  const canvasRef = useRef(null);
  const recognitionRef = useRef(null);

  // ------------------
  // 1) Speech-to-Text
  useEffect(() => {
    const SR = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (!SR) return;
    const rec = new SR();
    rec.lang = 'tr-TR';
    rec.interimResults = false;
    rec.onstart = () => setRecognizing(true);
    rec.onresult = (e) => {
      setText(e.results[0][0].transcript);
    };
    rec.onend = () => setRecognizing(false);
    recognitionRef.current = rec;
  }, []);

  // ------------------
  // 2) Kamera AÃ§ & FotoÄŸraf Ã‡ek
  const openCamera = async () => {
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      videoRef.current.srcObject = stream;
      await videoRef.current.play();
    } catch (e) {
      alert('Kameraya eriÅŸilemedi.');
      console.error(e);
    }
  };
  const capturePhoto = () => {
    if (!videoRef.current?.srcObject) return alert('Ã–nce kamera aÃ§Ä±n.');
    const ctx = canvasRef.current.getContext('2d');
    ctx.drawImage(videoRef.current, 0, 0, 160, 120);
    const b64 = canvasRef.current.toDataURL('image/png').split(',')[1];
    setImageData(b64);
  };

  // ------------------
  // 3) Analiz Et
  const analyze = async () => {
    if (recognizing) return alert('Ã–nce konuÅŸmayÄ± durdurun.');
    if (!text && !imageData) return alert('Metin ya da fotoÄŸraf girin.');
    setLoading(true);
    try {
      const res = await fetch(`${BACKEND_URL}/api/analyze`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          text: text || null,
          face: imageData || null,
          use_audio: false
        })
      });
      if (!res.ok) throw new Error(await res.text());
      const json = await res.json();
      onResults(json);
      // Temizle
      setText('');
      setImageData(null);
    } catch (err) {
      console.error(err);
      alert('Analiz sÄ±rasÄ±nda hata: ' + err.message);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="emotion-input">
      {/* Metin GiriÅŸi */}
      <textarea
        className="emotion-input__text"
        placeholder="Metni yazÄ±n veya mikrofonla kaydedin"
        value={text}
        onChange={e => setText(e.target.value)}
      />

      {/* Kontroller */}
      <div className="emotion-input__controls">
        {/* Sesle Yaz */}
        <div className="control-block">
          <button
            onClick={() => recognitionRef.current?.start()}
            disabled={recognizing || loading}
          >
            ðŸŽ¤ {recognizing ? 'Dinleniyorâ€¦' : 'Sesle Yaz'}
          </button>
          <button
            onClick={() => recognitionRef.current?.stop()}
            disabled={!recognizing}
          >
            âœ‹ Dur
          </button>
        </div>

        {/* Kamera */}
        <div className="control-block">
          <button onClick={openCamera} disabled={loading}>
            ðŸ“· Kamera AÃ§
          </button>
          <button
            onClick={capturePhoto}
            disabled={!videoRef.current?.srcObject || loading}
          >
            ðŸ“¸ FotoÄŸraf Ã‡ek
          </button>
          <video ref={videoRef} width="160" height="120" />
          <canvas
            ref={canvasRef}
            width="160"
            height="120"
            style={{ display: 'none' }}
          />
        </div>
      </div>

      {/* Analiz Et */}
      <button
        className="emotion-input__analyze"
        onClick={analyze}
        disabled={loading}
      >
        {loading ? 'Analiz Ediliyorâ€¦' : 'Analiz Et'}
      </button>
    </div>
  );
}
